    private void btningresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btningresarActionPerformed

        try {
            String email = txtemail.getText();
            String pass = txtpass.getText();
            UsuarioDAO dao = new UsuarioDAO();
            UsuarioBean usuario = dao.searchUsuario(email, pass);

            if (usuario == null) {
                JOptionPane.showMessageDialog(null, "usuario o password incorrectos");
            } else {
                if (usuario.getUsuatipo().equals("user")) {
                    JOptionPane.showMessageDialog(null, "Bienvenid@ " + usuario.getUsuanomb());
                    com.servicios.usuario.Dashboard dashboard = new com.servicios.usuario.Dashboard();
                    dashboard.setUsuario(usuario);
                    dashboard.setVisible(true);
                } else {
                    JOptionPane.showMessageDialog(null, "Bienvenid@ Administrador");
                    com.servicios.admin.AdminDashboard dashadmin = new com.servicios.admin.AdminDashboard();
                    dashadmin.setUsuario(usuario);
                    dashadmin.setVisible(true);
                }
                txtemail.setText(null);
                txtpass.setText(null);
                this.dispose();
            }
        } catch (SQLException ex) {
            Logger.getLogger(Ingresar.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_btningresarActionPerformed

